// src/app/components/product-list/product-list.component.ts

import { Component, OnInit } from '@angular/core';
import { ProductService } from '../../services/productos.service'; // Asegúrate de importar el servicio
import { Product } from '../../models/product.model';

@Component({
  selector: 'app-product-list',
  templateUrl: './product-list.component.html',
  styleUrls: ['./product-list.component.css']
})
export class ProductListComponent implements OnInit {
  private products = [
    {id: 1, nombre: "Mackbook Pro 2024", descripcion:"el ordenador para sobornar a la profe", colores: "silver"},
    {id: 2, nombre: "Imac de 2017", descripcion:"el modelo de Imac que representa a Apple", colores: "grey"},
    {id: 3, nombre: "Iphone12", descripcion:"No hace falta tener el último Iphone para llamar a tus amigos", colores: "yellow"}
  ];


  public _products: Product[] = [];  // Cambiar a pública
  isFormVisible: boolean = false;
  selectedProduct: Product | null = null;
  productsList: Product[] = [];

  constructor(private productService: ProductService) {}
  ngOnInit(): void {
    // Cargar productos cuando el componente se inicializa
    this.loadProducts();
  }

  // Cargar productos desde el servicio
  loadProducts(): void {
    this.products = this.productService.getProducts();  // Obtener productos del servicio
  }

  // Mostrar formulario para agregar o editar un producto
  showProductForm(): void {
    this.isFormVisible = true;
    this.selectedProduct = null; // Resetea para agregar un nuevo producto
  }


  // Editar producto
  editProduct(id: number): void {
    this.productService.editProduct(id);
    this.products = this.productService.getProducts(); 
  }

  // Eliminar producto
  deleteProduct(id: number): void {
    this.productService.deleteProduct(id);
    this.products = this.productService.getProducts();  // Refrescamos la lista de productos
  }
}
<div class="container mt-4">
    <h2>Lista de Productos</h2>
    <button  class="btn btn-primary mb-4">Agregar Producto</button>

    <!-- Tabla de productos -->
    <table class="table table-striped">
      <thead>
        <tr>
          <th>ID</th>
          <th>Nombre</th>
          <th>Descripción</th>

          <th>Colores</th>
        </tr>
      </thead>
      <tbody>
        <tr *ngFor="let product of products">
          <td>{{ product.id }}</td>
          <td>{{ product.nombre }}</td>
          <td>{{ product.descripcion }}</td>
          <td>{{ product.colores }}</td>
          <td>
            <!-- Botón de editar  -->
            <button (click)="editProduct(product.id)" class="btn btn-warning btn-sm">Editar</button>
            <!-- Botón de eliminar -->
            <button (click)="deleteProduct(product.id)" class="btn btn-danger btn-sm">Eliminar</button>
          </td>
        </tr>
      </tbody>
    </table>
  </div>
