// src/app/components/product-list/product-list.component.ts

import { Component, OnInit } from '@angular/core';
import { ProductService } from '../../services/productos.service'; // Asegúrate de importar el servicio

@Component({
  selector: 'app-product-list',
  templateUrl: './product-list.component.html',
  styleUrls: ['./product-list.component.css']
})
export class ProductListComponent implements OnInit {
  private products = [
    {id: 1, nombre: "Mackbook Pro 2024", descripcion:"el ordenador para sobornar a la profe", colores: ["silver", "grey", "white"]},
    {id: 2, nombre: "Imac de 2017", descripcion:"el modelo de Imac que representa a Apple", colores: "grey"},
    {id: 3, nombre: "Iphone12", descripcion:"No hace falta tener el último Iphone para llamar a tus amigos", colores: ["yellow", "blue", "purple"]}
  ];

  constructor(private productService: ProductService) {}

  ngOnInit(): void {
    this.products = this.productService.getProducts();
  }

  // Mostrar formulario para agregar o editar un producto
  showProductForm(): void {
    this.isFormVisible = true;
    this.selectedProduct = null; // Esto lo resetea para agregar un nuevo producto
  }

  // Editar producto
  editProduct(id: number): void {
    this.productService.editProduct(id);
    this.products = this.productService.getProducts(); 
  }

  // Eliminar producto
  deleteProduct(id: number): void {
    this.productService.deleteProduct(id);
    this.products = this.productService.getProducts();  // Refrescamos la lista de productos
  }
}
